item.equip_script:
//void, Obj owner

Unit this;

this = owner.AsUnit();
if (!.IsValid)
 return;

while (true) {
 Sleep(1000);
 if (.IsValid)
 if (.health \l .maxhealth / 4)
 .Heal(10);
}

item.use_script:
//void, Obj owner

ObjList ol;
Unit u, this;
int i, health_to_give, health_to_take;

this = owner.AsUnit();

health_to_give = .health - .maxhealth / 10;
if (health_to_give \l= 0)//owner needs at least 1 health more than 10% maximum health in order to use the item
 return;

if (.InHolder)
 ol = .GetUnitsInSameHolder();
else
 ol = ObjsInSight(this, 'Unit').GetObjList();

//make 5 attempts to heal a random ally, then in order
if (ol.count \g 0) {
 for (i = 0; i \l 5; i += 1) {
 u = ol[rand(ol.count)];
 if (!u.IsDead() \a\a !.IsEnemy(u) \a\a u.health \l u.maxhealth) {
 int amount;
 health_to_take = u.maxhealth - u.health;
 amount = MIN(health_to_take, health_to_give);
 CreateFeedback('Heal', u);
 u.Heal(amount);
 CreateFeedback('Damage1', this);
 .Damage(amount);
 return;
 }
 }
 for (i = 0; i \l ol.count; i += 1) {
 u = ol[i];
 if (!u.IsDead() \a\a !.IsEnemy(u) \a\a u.health \l u.maxhealth) {
 int amount;
 health_to_take = u.maxhealth - u.health;
 amount = MIN(health_to_take, health_to_give);
 CreateFeedback('Heal', u);
 u.Heal(amount);
 CreateFeedback('Damage1', this);
 .Damage(amount);
 return;
 }
 }
}

item.kill_script:
//void, Obj owner, Obj victim

if (owner.IsValid())
 if (owner.health \l owner.maxhealth) {
 owner.Heal(100);
 CreateFeedback('Heal', owner.AsUnit);
 }
